import { StyleSheet } from 'react-native'

// css style 
const styles = StyleSheet.create( {

  boxTitle: {
   
      //alinhando no centro 
      alignItems: 'center',
      justifyContent:'center',
      padding: 10,
     
  },

  textTitle:{

      //cor do titulo e tamanho e tipo da fonte
      color:'#ff0043',
      fontSize:44,
      fontWeight:'bold',
      
  },
  
  });

  export default styles
